items.scopedStyle:
/* 编辑态-输入框样式 */
:scope .wd-form-input-wrap {
  font-size: 18px;
  color: #000;
  font-weight: bold;
}
:root .wd-form-item--weui.wd-form-item {
  padding: 0;
}
:root .wd-form-item--weui .wd-form-item-wrap {
  border: none;
  border-bottom: none !important;
}
:root .wd-form-item--weui .wd-form-item-wrap__control {
  padding: 0;
  min-height: unset;
}

:root .wd-form-item.wd-form-item--horizontal-right .wd-form-item-wrap {

}
/* 只读态-表单值样式 */
:scope .wd-form-item__readonly-value {
  font-size: 18px;
  color: #000;
  font-weight: bold;
}
/* 编辑态-输入框样式 */
:scope .wd-form-input-wrap {
  border: none;
  height: 40px;
  width: 180px;
}
/* 编辑态-输入框样式（获取焦点） */
:scope .wd-form-input-wrap.is-focused {
  border: 1px solid gray;
  background-color: transparent;
}
/* 编辑态-输入框样式 */
:scope .wd-form-input-wrap {
  padding: calc(var(--wd-space-base)*2);
}

:scope {
  color: #4C2878;
  background: #fff;
  min-height: unset;
  padding: 4px var(--wd-space-base-md, 12px);
  border: none;
  border-radius: 56px;
}
