handler.:code:
({event}) => {
  let retryTimes = 0;
  let timer = null;
  $w.app.common.requestPayment({ target: $w.item_listView1 }, async () => {
     if ($w.app.common.dealPayState({ type: 'get' }) === 'success') {
      $w.utils.showLoading({ title: '请稍后...'})
      await $w.queryBalance.trigger();
      timer = setInterval(async () => {
        if (retryTimes > 5) {
          retryTimes = 0;
          timer && clearInterval(timer);
          return;
        }
        retryTimes += 1;
        await $w.queryBalance.trigger();
        await $w.app.common?.getVideoUserInfo?.();
        if (retryTimes === 3) {
          $w.utils.showToast({ title: '充值已到帐，如未更新请刷新重试', icon: 'none'})
        }
      }, 2000);
      $w.app.common.dealPayState({ value: '' })
    }
  })
}

scopedStyle:
:scope::before {
  content: "";
  display: block;
  position: fixed;
  right: 112rpx;
  top: -22rpx;
  width: 0;
  height: 0;
  border-top: 6px solid transparent;
  border-left: 6px solid rgb(185, 213, 248);
  border-bottom: 6px solid transparent;
  border-right: 6px solid transparent;
  transform: rotate(-90deg);
  z-index: 10;
}

@keyframes zoomIn {
  0% {
    transform: scale3d(1, 1, 1);
  }
  50% {
    transform: scale3d(1.02, 1.02, 1.02);
  }
  100% {
    transform: scale3d(1, 1, 1);
  }
}
